import React, { Component } from 'react';
import Pagination from "react-js-pagination";
// import SearchTracks from './SearchTracks';

class Tracks extends Component {
    state = {
        playing: false,
        activePage: 1,
        itemsCountPerPage: 2,
        totalItemsCount: 0,
        audio: null,
        playingPreviewUrl: null,
        searchedTracks: [],
        TracksQeury: "",
        SearchStatus:true
    };
    componentDidUpdate(prevProps,prevState,snapshot){
        if(this.props.tracks!==prevProps.tracks){
            this.setState({TracksQeury:"", searchedTracks:this.props.tracks, SearchStatus:true});

        }
    }

    updateTrackQuery = event => {
        this.setState({activePage:1});
        this.state.TracksQeury=event.target.value;
        if(this.state.TracksQeury===""){
            this.setState({searchedTracks:this.props.tracks});
        }else{
            this.searchTracks();
        }
    }


    searchTracks = () => {
        
            const searchedTracks = this.props.tracks.filter(
                track => track.name.toLowerCase().indexOf(this.state.TracksQeury.toLowerCase()) !== -1
            );
                this.setState({ searchedTracks: searchedTracks, SearchStatus:true });

            // if(!searchedTracks.length){
            //     this.setState({SearchStatus:false});
            // }else{
            //     this.setState({ searchedTracks: searchedTracks, SearchStatus:true });
            // }
        }
    // searchResults = searchedTracks => {
    //     console.log(this.state);
    // };
    playAudio = previewUrl => () => {
        const audio = new Audio(previewUrl);
        if (!this.state.playing) {
            audio.play();
            this.setState({ playing: true, audio, playingPreviewUrl: previewUrl });
        } else {
            this.state.audio.pause();
            if (this.state.playingPreviewUrl === previewUrl) {
                this.setState({ playing: false });
            } else {
                audio.play();
                this.setState({ audio, playingPreviewUrl: previewUrl });
            }
        }
    }



    handlePageChange(pageNumber) {
        this.setState({ activePage: pageNumber });
    }

    // componentDidMount() {


        //     const {tracks}=this.props;
        //     console.log(tracks);
        //     this.setState({TempTrack:tracks.slice(1,4)})
        //     console.log(this.state.TempTracks);
    // }

    trackIcon = track => {
        if (!track.preview_url) {
            return <span>N/A</span>;
        }
        if (this.state.playing && this.state.playingPreviewUrl === track.preview_url) {
            return <span> | | </span>;
        }

        return <span>&#9654;</span>;
    }

    render() {
        // let { tracks } = this.props;
        const endIndex = this.state.activePage * this.state.itemsCountPerPage;
        const startIndex = endIndex - this.state.itemsCountPerPage;
        // if (this.state.searchedTracks.length) {
        //     tracks = this.state.searchedTracks;
        // }
        const temptracks = this.state.searchedTracks.slice(startIndex, endIndex);
        return (
            <div>

                <div>
                    <hr />
                    <div>
                        <input
                            placeholder="search for a Track"
                            onChange={this.updateTrackQuery}
                            value={this.state.TracksQeury}
                        />
                    </div>
                    {/* <SearchTracks tracks={this.props.tracks} tracksState={this.state} searchResults={this.searchResults}/> */}
                    {this.state.searchedTracks.length?
                        <div>
                            {
                             temptracks.map(track => {
                            const { id, name, album, preview_url } = track;
                            return (
                                <div
                                    key={id}
                                    className='track'
                                    onClick={this.playAudio(preview_url)}
                                >
                                    <img
                                        className='track-image'
                                        src={album.images[0].url}
                                        alt='track-image'
                                    />
                                    <p className='track-text'>{name}</p>
                                    <p className='track-icon' >{this.trackIcon(track)}</p>

                                </div>
                            )
                        })

                    }
                                    <div>
                    {/* from https://www.npmjs.com/package/react-js-pagination */}
                    <Pagination className='pagination'
                        activePage={this.state.activePage}
                        itemsCountPerPage={this.state.itemsCountPerPage}
                        totalItemsCount={this.state.searchedTracks.length}
                        pageRangeDisplayed={5}
                        onChange={this.handlePageChange.bind(this)}
                    />
                </div>
                            </div>
                        
                        :                
                            <div>
                            <h3>Error</h3>
                            <p>No Tracks Found for {this.state.TracksQeury}</p>
                            </div>
                        }
                </div>

            </div>
        )

    }
    
}

export default Tracks;

